mapper.select:
select

,

from jht_meeting


    and USER_ID = #{userId}



    and (
       HOST_USER_ID = #{hostUserId}

      or SUMMARY_USER_ID = #{summaryUserId}

      or USER_IDS like CONCAT('%',#{userIds},',%')
      or USER_IDS like CONCAT(#{userIds},',%')
    )



      and MANAGER_ID = #{managerId}



        and MEET_STATUS = #{meetStatus}


        and (
            DEPT_ID like CONCAT('%',#{deptId},',%')
          or DEPT_ID like CONCAT(#{deptId},',%')
          or DEPT_ID = 'ALL_DEPT'
        )
