mapper.select:
SELECT
AFF_ID,USER_ID, BEGIN_TIME, END_TIME, TYPE, REMIND_DATE, REMIND_TIME,LAST_REMIND, SMS_REMIND,SMS2_REMIND,
LAST_SMS2_REMIND, MANAGER_ID,
CAL_TYPE, ADD_TIME,BEGIN_TIME_TIME,END_TIME_TIME, ALLDAY, CONTENT, TAKER

  ,sca.CONTENT_SECRECY

from affair a

  LEFT JOIN security_content_approval sca ON sca.MODULE_TABLE = 'affair' AND a.AFF_ID = sca.RECORD_ID AND sca.OPERATION_TYPE = 0


WHERE 1=1




   AND BEGIN_TIME >= #{beginTime}


    AND END_TIME <= #{endTime}



    and CONTENT like concat('%',#{content},'%')


    and AFF_ID =#{affId}


    and (a.TAKER like concat('%,',#{userId},',%') or a.TAKER like concat('%',#{userId},',%') or a.USER_ID =#{userId}
    )


    AND CAL_TYPE =#{calType}


  AND ((((sca.APPROVAL_STATUS = 1

   AND (USER_ID != #{userId} AND TAKER LIKE concat('%,', #{userId} , ',%'))

   )

    OR (sca.APPROVAL_STATUS != '1' AND sca.OPERATION_USER_ID = #{scaOperationUserId})

  )
  AND sca.CONTENT_SECRECY in

        #{security}

    ) or a.USER_ID =#{scaOperationUserId})

SELECT
AFF_ID,USER_ID, BEGIN_TIME, END_TIME, TYPE, REMIND_DATE, REMIND_TIME,LAST_REMIND, SMS_REMIND,SMS2_REMIND,
LAST_SMS2_REMIND, MANAGER_ID,
CAL_TYPE, ADD_TIME,BEGIN_TIME_TIME,END_TIME_TIME, ALLDAY, CONTENT, TAKER

  ,sca.CONTENT_SECRECY

from affair a

  LEFT JOIN security_content_approval sca ON sca.MODULE_TABLE = 'affair' AND a.AFF_ID = sca.RECORD_ID AND sca.OPERATION_TYPE = 0


WHERE 1=1




   AND BEGIN_TIME >= #{beginTime}


    AND END_TIME <= #{endTime}



    and CONTENT like concat('%',#{content}::VARCHAR,'%')


    and AFF_ID =#{affId}


    and (a.TAKER like concat('%,',#{userId}::VARCHAR,',%') or a.TAKER like concat('%',#{userId}::VARCHAR,',%') or a.USER_ID =#{userId}
    )


    AND CAL_TYPE =#{calType}


    AND ((((sca.APPROVAL_STATUS = 1

     AND (USER_ID != #{userId} AND TAKER LIKE concat('%,', #{userId}::VARCHAR , ',%'))

     )

    OR (sca.APPROVAL_STATUS != '1' AND sca.OPERATION_USER_ID = #{scaOperationUserId})

  )
  AND sca.CONTENT_SECRECY in

        #{security}

     ) or a.USER_ID =#{scaOperationUserId})

SELECT
AFF_ID,USER_ID, BEGIN_TIME, END_TIME, TYPE, REMIND_DATE, REMIND_TIME,LAST_REMIND,  SMS_REMIND,SMS2_REMIND, LAST_SMS2_REMIND, MANAGER_ID,
CAL_TYPE, ADD_TIME,BEGIN_TIME_TIME,END_TIME_TIME, ALLDAY, CONTENT, TAKER
from   affair
WHERE 1=1

  BEGIN_TIME  >=  #{beginTime1}


  AND END_TIME  <= #{endTime1}


  AND  CONTENT =#{content}


  AND  TAKER like concat('%',#{taker},'%')

SELECT
count(*)
from   affair
WHERE 1=1

  BEGIN_TIME  >=  #{beginTime}


  AND END_TIME  <= #{endTime}


  AND  CONTENT =#{content}


  AND  TAKER like concat('%',#{taker},'%')
