mapper.select:
select count(*) from ${company}.`user`
where DEPT_ID = #{deptId}
or DEPT_ID_OTHER like CONCAT('%',#{deptId},'%')

select u.UID, u.USER_ID, u.USER_NAME, u.PASSWORD, u.BYNAME, u.MOBIL_NO, u.EMAIL, u.THEME, u.SEX, u.DEPT_ID, u.USER_PRIV, u.USER_PRIV_NAME, al.EXTERNALID from ${company}.`user` u, ${company}.user_alidaas al
u.USER_ID = al.USER_ID and al.EXTERNALID = #{externalId}
u.USER_ID = al.USER_ID and u.USER_ID = #{userId}
