mapper.select:
select
lims_equip_eventlog.*,department.DEPT_NAME
from lims_equip_eventlog left join department on lims_equip_eventlog.DEPT_ID=department.DEPT_ID


    ${LimsEquipEventlog.columnName} like CONCAT('%',#{LimsEquipEventlog.inputValue},'%')


    and EQUIP_TYPE_ID IN

      #{id}



    and EQUIP_ID=#{LimsEquipEventlog.equipId}


  and EQUIP_EVENT_TYPE=#{LimsEquipEventlog.equipEventType}
