mapper.select:
SELECT

FROM lims_equip_inventory
WHERE
INVE_ID=#{inveId}

  AND INVE_STATUS=#{inveStatus}

mapper.delete:
DELETE from lims_equip_inventory WHERE 1=1
AND INVE_ID IN
#{inveId}

mapper.update:
UPDATE lims_equip_inventory


    WHETHER_CHANGES=#{whetherChanges},


   INVE_STATUS = #{inveStatus},


    CHECK_TIME = #{checkTime,jdbcType=TIMESTAMP}


where 1=1

  AND  DETAIL_ID = #{detailId}


  AND INVE_ID=#{inveId}
