mapper.select:
SELECT ls.*, lg.RECEIVE_USER, us.USER_NAME FROM lims_equip_ownerlist ls LEFT JOIN lims_equip_ownerlog lg ON ls.OWNERLOG_ID = lg.OWNERLOG_ID LEFT JOIN user us ON lg.RECEIVE_USER=us.USER_ID WHERE 1=1
AND ls.OWNERLOG_ID=#{ownerlogId}
AND ls.RECEIVE_STATUS=#{receiveStatus}
AND ls.OWNERLOG_ID IN
#{ownerlogId}

mapper.delete:
DELETE FROM lims_equip_ownerlist WHERE 1=1
AND OWNERLIST_ID IN
#{ownerlistId}
AND OWNERLOG_ID IN
#{ownerlogId}

mapper.update:
 UPDATE lims_equip_ownerlist


     RECEIVE_TIME = #{receiveTime,jdbcType=TIMESTAMP},


     NEW_POSITION_ID = #{newPositionId,jdbcType=INTEGER},


     NEW_POSITION_NAME = #{newPositionName,jdbcType=VARCHAR},


     MEMO = #{memo,jdbcType=VARCHAR},


     RECEIVE_STATUS = #{receiveStatus,jdbcType=VARCHAR},


where 1=1

AND  OWNERLIST_ID =#{ownerlistId}


   AND OWNERLIST_ID IN

     #{ownerlistId}



 AND  OWNERLOG_ID =#{ownerlogId}


   AND OWNERLOG_ID IN

     #{ownerlogId}
