mapper.select:
select
,
from meeting where SID = #{sid,jdbcType=INTEGER}

SELECT m.* FROM meeting m


        and m.MEET_NAME like CONCAT('%',#{meetingWithBLOBs.meetName},'%')


        and m.START_TIME >= #{meetingWithBLOBs.startTime}


        and m.START_TIME <= #{meetingWithBLOBs.endTime}


        and m.STATUS=#{meetingWithBLOBs.status}


        and m.MEET_ROOM_ID=#{meetingWithBLOBs.meetRoomId}


        AND m.STATUS IN (2,5)


        and UID=#{meetingWithBLOBs.uid}


        and (m.START_TIME <= #{meetingWithBLOBs.currentTime} and #{meetingWithBLOBs.currentTime} <
        m.END_TIME) AND m.STATUS NOT IN (1,4,5)


        and (((m.START_TIME <= #{meetingWithBLOBs.currentTime} and #{meetingWithBLOBs.currentTime} >
        m.END_TIME) AND m.STATUS NOT IN (1,4)) OR m.STATUS='5')


        and m.CYCLE = #{meetingWithBLOBs.cycle}


        and m.CYCLE_NO = #{meetingWithBLOBs.cycleNo}



    GROUP BY m.CYCLE_NO

order by m.CREATE_TIME desc

SELECT * FROM meeting
and MEET_NAME like concat(concat('%,',#{meetingWithBLOBs.meetName}, ',%')
and START_TIME >= to_date(#{meetingWithBLOBs.startTime})
and START_TIME <= to_date(#{meetingWithBLOBs.endTime})
and STATUS=#{meetingWithBLOBs.status}
and MEET_ROOM_ID=#{meetingWithBLOBs.meetRoomId}
AND STATUS IN (2,5)
and UID=#{meetingWithBLOBs.uid}
and MANAGER_ID=#{meetingWithBLOBs.managerId}
and (START_TIME <= to_date(#{meetingWithBLOBs.currentTime},'yyyy-mm-dd hh24:mi:ss') and to_date(#{meetingWithBLOBs.currentTime},'yyyy-mm-dd hh24:mi:ss') < END_TIME) AND STATUS NOT IN (1,4,5)
and (((START_TIME <=to_date(#{meetingWithBLOBs.currentTime},'yyyy-mm-dd hh24:mi:ss') and to_date(#{meetingWithBLOBs.currentTime},'yyyy-mm-dd hh24:mi:ss') > END_TIME) AND STATUS NOT IN (1,4)) OR STATUS='5')
order by CREATE_TIME desc

