mapper.update:
update
meeting
SET
STATUS=#{status}

    ,REASON=#{reason}

WHERE
SID=#{sid}

mapper.select:
SELECT count(*) FROM (SELECT m.* FROM meeting m


        and m.MEET_NAME like CONCAT('%',#{meetName},'%')


        and m.START_TIME >= #{startTime}


        and m.START_TIME <= #{endTime}


        and m.STATUS=#{status}


        and m.MEET_ROOM_ID=#{meetRoomId}


        AND m.STATUS IN (2,5)


        and (UID=#{uid} or RECORDER_ID=#{uid} OR ATTENDEE LIKE CONCAT('%,',#{uid},',%') OR ATTENDEE LIKE
        CONCAT(#{uid},',%'))


        and (m.START_TIME <= #{currentTime} and #{currentTime} < m.END_TIME) AND m.STATUS NOT IN (1,4,5)


        and (((m.START_TIME <= #{currentTime} and #{currentTime} > m.END_TIME) AND m.STATUS NOT IN (1,4))
        OR m.STATUS='5')


        and m.CYCLE = #{cycle}


        and m.CYCLE_NO = #{cycleNo}



    GROUP BY m.CYCLE_NO

order by m.CREATE_TIME desc
) t

SELECT count(*) FROM (SELECT m.* FROM meeting m where


        1=1

            and STATUS=#{status}


            and m.CYCLE = #{cycle}


            and m.CYCLE_NO = #{cycleNo}



        1=1

            and MANAGER_ID=#{uid}


            and STATUS=#{status}


            and m.CYCLE = #{cycle}


            and m.CYCLE_NO = #{cycleNo}



order by START_TIME desc) t

SELECT count(*) FROM meeting
and STATUS=#{status}
and MANAGER_ID=#{managerId}
AND STATUS IN (2,5)
and (START_TIME <= to_date(#{currentTime},'yyyy-mm-dd hh24:mi:ss') and to_date(#{currentTime},'yyyy-mm-dd hh24:mi:ss') < END_TIME) AND STATUS NOT IN (1,4,5)
and (((START_TIME <= to_date(#{currentTime},'yyyy-mm-dd hh24:mi:ss') and to_date(#{currentTime},'yyyy-mm-dd hh24:mi:ss') > END_TIME) AND STATUS NOT IN (1,4)) OR STATUS=5)

SELECT count(*) FROM meeting
and STATUS=#{status}
and MANAGER_ID=#{managerId}
AND STATUS IN (2,5)
and (START_TIME <= to_date(#{currentTime},'yyyy-mm-dd hh24:mi:ss') and to_date(#{currentTime},'yyyy-mm-dd hh24:mi:ss') < END_TIME) AND STATUS NOT IN (1,4,5)
and (((START_TIME <= to_date(#{currentTime},'yyyy-mm-dd hh24:mi:ss') and to_date(#{currentTime},'yyyy-mm-dd hh24:mi:ss') > END_TIME) AND STATUS NOT IN (1,4)) OR STATUS=5)

SELECT * from meeting
WHERE
   MEET_ROOM_ID=#{meetRoomId}
   and
   (
      START_TIME < #{currentBeginTime} and END_TIME >= #{currentEndTime}
      or
      START_TIME >= #{currentBeginTime} and END_TIME < #{currentEndTime}
      or
      START_TIME <= #{currentBeginTime} and END_TIME < #{currentEndTime} and END_TIME > #{currentBeginTime}
      or
      START_TIME >= #{currentBeginTime} and START_TIME < #{currentEndTime} and END_TIME > #{currentEndTime}
    )

SELECT * FROM meeting

    STATUS IN (2,3)

        and MEET_NAME like CONCAT('%#{meetingWithBLOBs.meetName}','%')


        and START_TIME >= #{meetingWithBLOBs.startTime}


        and START_TIME <= #{meetingWithBLOBs.endTime}


order by CREATE_TIME desc

SELECT * FROM meeting

    STATUS IN (2,3)

        and MEET_NAME like CONCAT('%#{meetingWithBLOBs.meetName}','%')


        and START_TIME >= #{meetingWithBLOBs.startTime}


        and START_TIME <= #{meetingWithBLOBs.endTime}


order by CREATE_TIME desc

SELECT * FROM meeting m where 1=1 AND ( (ATTENDEE LIKE concat('%,',#{meetingWithBLOBs.uid},',%') OR ATTENDEE LIKE concat(#{meetingWithBLOBs.uid},',%')) OR (ATTENDEE_OUT LIKE concat('%,',#{meetingWithBLOBs.uid},',%') OR ATTENDEE_OUT LIKE concat(#{meetingWithBLOBs.uid},',%') ) OR (RECORDER_ID = #{meetingWithBLOBs.uid} OR RECORDER_ID LIKE concat('%,',#{meetingWithBLOBs.uid},',%') OR RECORDER_ID LIKE concat(#{meetingWithBLOBs.uid},',%') ) OR (SERVICE_USER = #{meetingWithBLOBs.uid} OR SERVICE_USER LIKE concat('%,',#{meetingWithBLOBs.uid},',%') OR SERVICE_USER LIKE concat(#{meetingWithBLOBs.uid},',%') ) OR UID = #{meetingWithBLOBs.uid} )
and m.MEET_NAME like CONCAT('%',#{meetingWithBLOBs.meetName},'%')
and m.START_TIME >= #{meetingWithBLOBs.startTime}
and m.START_TIME <= #{meetingWithBLOBs.endTime}
and STATUS in (2,3,5) order by START_TIME desc

SELECT * FROM meeting m where 1=1 AND ( (ATTENDEE LIKE concat('%,',#{meetingWithBLOBs.uid}::VARCHAR,',%') OR ATTENDEE LIKE concat(#{meetingWithBLOBs.uid}::VARCHAR,',%')) OR (ATTENDEE_OUT LIKE concat('%,',#{meetingWithBLOBs.uid}::VARCHAR,',%') OR ATTENDEE_OUT LIKE concat(#{meetingWithBLOBs.uid}::VARCHAR,',%') ) OR (RECORDER_ID = #{meetingWithBLOBs.uid} OR RECORDER_ID LIKE concat('%,',#{meetingWithBLOBs.uid}::VARCHAR,',%') OR RECORDER_ID LIKE concat(#{meetingWithBLOBs.uid}::VARCHAR,',%') ) OR (SERVICE_USER = #{meetingWithBLOBs.uid} OR SERVICE_USER LIKE concat('%,',#{meetingWithBLOBs.uid}::VARCHAR,',%') OR SERVICE_USER LIKE concat(#{meetingWithBLOBs.uid}::VARCHAR,',%') ) OR UID = #{meetingWithBLOBs.uid} )
and m.MEET_NAME like CONCAT('%',#{meetingWithBLOBs.meetName}::VARCHAR,'%')
and m.START_TIME >= #{meetingWithBLOBs.startTime}
and m.START_TIME <= #{meetingWithBLOBs.endTime}
and STATUS in (2,3,5) order by START_TIME desc

SELECT m.* FROM meeting m where


        1=1

            and m.UID = #{meetingWithBLOBs.uid}


            and m.MEET_NAME LIKE CONCAT('%',#{meetingWithBLOBs.meetName},'%')


            and m.START_TIME >= cast(concat( #{meetingWithBLOBs.startTime},' 00:00:00') AS datetime)


            and m.START_TIME <= cast(concat( #{meetingWithBLOBs.endTime},' 23:59:59') AS datetime)


            and STATUS = #{meetingWithBLOBs.status}


            and m.MEET_ROOM_ID = #{meetingWithBLOBs.meetRoomId}


            and m.CYCLE = #{meetingWithBLOBs.cycle}


            and m.CYCLE_NO = #{meetingWithBLOBs.cycleNo}



        1=1

            and m.UID = #{meetingWithBLOBs.uid}


            and m.MEET_NAME LIKE CONCAT('%',#{meetingWithBLOBs.meetName},'%')


            and m.START_TIME >= cast(concat( #{meetingWithBLOBs.startTime},' 00:00:00') AS datetime)


            and m.START_TIME <= cast(concat( #{meetingWithBLOBs.endTime},' 23:59:59') AS datetime)


            and STATUS = #{meetingWithBLOBs.status}


            and m.MEET_ROOM_ID = #{meetingWithBLOBs.meetRoomId}


            and MANAGER_ID = #{meetingWithBLOBs.managerId}


            and m.CYCLE = #{meetingWithBLOBs.cycle}


            and m.CYCLE_NO = #{meetingWithBLOBs.cycleNo}





        order by START_TIME


        order by START_TIME desc

SELECT m.* FROM meeting m left join meeting_room mr on m.MEET_ROOM_ID = mr.SID where


        1=1

            and m.UID=#{meetingWithBLOBs.uid}


            and m.MEET_NAME like CONCAT('%',#{meetingWithBLOBs.meetName},'%')


            and m.START_TIME >= cast(concat( #{meetingWithBLOBs.startTime},' 00:00:00') AS datetime)


            and m.START_TIME <= cast(concat( #{meetingWithBLOBs.endTime},' 23:59:59') AS datetime)


            and m.STATUS=#{meetingWithBLOBs.status}


            and m.MEET_ROOM_ID = #{meetingWithBLOBs.meetRoomId}



        1=1

            and ( m.UID=#{meetingWithBLOBs.uid} OR (m.ATTENDEE LIKE concat('%,',#{meetingWithBLOBs.uid},',%')
            OR m.ATTENDEE LIKE concat(#{meetingWithBLOBs.uid},',%')
            )OR
            (m.ATTENDEE_OUT LIKE concat('%,',#{meetingWithBLOBs.uid},',%')
            OR m.ATTENDEE_OUT LIKE concat(#{meetingWithBLOBs.uid},',%')
            )OR
            (m.RECORDER_ID = #{meetingWithBLOBs.uid} OR m.RECORDER_ID LIKE
            concat('%,',#{meetingWithBLOBs.uid},',%')
            OR m.RECORDER_ID LIKE concat(#{meetingWithBLOBs.uid},',%')
            )OR (m.SERVICE_USER = #{meetingWithBLOBs.uid} OR m.SERVICE_USER LIKE
            concat('%,',#{meetingWithBLOBs.uid},',%')
            OR m.SERVICE_USER LIKE concat(#{meetingWithBLOBs.uid},',%'))
            OR (mr.managerIds LIKE concat('%,',#{meetingWithBLOBs.uid},',%') OR mr.managerIds LIKE
            concat(#{meetingWithBLOBs.uid},',%'))
            )


            and m.MEET_NAME like CONCAT('%',#{meetingWithBLOBs.meetName}::VARCHAR,'%')


            and m.START_TIME >= #{meetingWithBLOBs.startTime}


            and m.START_TIME <= #{meetingWithBLOBs.endTime}


            and m.STATUS=#{meetingWithBLOBs.status}


            and m.MEET_ROOM_ID = #{meetingWithBLOBs.meetRoomId}



order by m.START_TIME desc

SELECT m.* FROM meeting m


        and m.MEET_NAME like CONCAT('%',#{meetingWithBLOBs.meetName},'%')


        and m.START_TIME >= #{meetingWithBLOBs.startTime}


        and m.START_TIME <= #{meetingWithBLOBs.endTime}


        and m.STATUS=#{meetingWithBLOBs.status}


        and m.MEET_ROOM_ID=#{meetingWithBLOBs.meetRoomId}


        AND m.STATUS IN (2,5)


        and UID=#{meetingWithBLOBs.uid}


        and (m.START_TIME <= #{meetingWithBLOBs.currentTime} and #{meetingWithBLOBs.currentTime} <
        m.END_TIME) AND m.STATUS NOT IN (1,4,5)


        and (((m.START_TIME <= #{meetingWithBLOBs.currentTime} and #{meetingWithBLOBs.currentTime} >
        m.END_TIME) AND m.STATUS NOT IN (1,4)) OR m.STATUS='5')


        and m.CYCLE = #{meetingWithBLOBs.cycle}


        and m.CYCLE_NO = #{meetingWithBLOBs.cycleNo}

    AND MEET_ROOM_ID in

        #{meetingRoom}



    GROUP BY m.CYCLE_NO

order by m.CREATE_TIME desc
