SELECT
n.*
FROM ${tableName} n
LEFT JOIN `user` u ON n.FROM_ID = u.USER_ID
LEFT JOIN department d ON u.DEPT_ID = d.DEPT_ID

	 AND n.FORMAT = #{format} 
	 AND n.TO_ID = #{toId} 
	 AND (n.TYPE_ID = '' OR  n.TYPE_ID IS  NULL ) 
	 AND n.TYPE_ID = #{typeId} 
	 AND n.SUBJECT like CONCAT('%',#{subject}::VARCHAR,'%') 

		AND DATE(n.SEND_TIME) = #{sendTime}


		AND n.PUBLISH = #{publish}




				#{str}




		AND n.FROM_ID = #{fromId}


		AND n.FROM_DEPT = #{fromDept}


		AND n.TOP = #{top}


		AND n.SEND_TIME >= #{beginDate}


		AND n.SEND_TIME <= #{endDate}

	 AND n.CONTENT like CONCAT('%',#{content}::VARCHAR,'%') 

		AND u.DEPT_ID IN

			#{deptId}



ORDER BY n.TOP DESC , n.SEND_TIME DESC

