mapper.select:
select

from task_center


    and USER_ID = #{userId,jdbcType=VARCHAR}


    and TASK_STATUS = #{taskStatus,jdbcType=VARCHAR}


    and FUNC_ID = #{funcId,jdbcType=INTEGER}


    and PLAN_END_TIME < now()


    AND FUNC_ID in

      #{id}



ORDER BY TASK_LEVEL DESC,BEGIN_TIME
limit #{page},#{pageSize}

select
count(*)
from task_center


    and USER_ID = #{userId,jdbcType=VARCHAR}


    and TASK_STATUS = #{taskStatus,jdbcType=VARCHAR}


    and FUNC_ID = #{funcId,jdbcType=INTEGER}


    and PLAN_END_TIME < now()


    AND FUNC_ID in

      #{id}



ORDER BY TASK_LEVEL DESC,BEGIN_TIME

SELECT IFNULL( ( IFNULL( sum( dataCount ), 0 ) ) / count( * ), 0 ) globalCount FROM ( SELECT USER_ID, IFNULL(count(*),0) AS dataCount FROM task_center WHERE TASK_STATUS = 1
AND END_TIME between #{monthBeginTime} AND #{monthEndTime}
GROUP BY USER_ID ) t

SELECT IFNULL(count(*),0) AS dataCount FROM task_center WHERE TASK_STATUS = 1

  AND END_TIME between #{monthBeginTime} AND #{monthEndTime}


  and USER_ID = #{userId,jdbcType=VARCHAR}

SELECT ifnull(( TIMESTAMPDIFF( second, BEGIN_TIME, END_TIME ) /3600)/count(*),0.00) FROM task_center WHERE TASK_STATUS = 1
AND END_TIME between #{monthBeginTime} AND #{monthEndTime}
and USER_ID = #{userId,jdbcType=VARCHAR}

SELECT FUNC_ID, IFNULL(count( * ),0) as DATA_COUNT FROM task_center


    and USER_ID = #{userId,jdbcType=VARCHAR}


    and TASK_STATUS = #{taskStatus,jdbcType=VARCHAR}


    AND END_TIME between #{monthBeginTime} AND #{monthEndTime}


    AND FUNC_ID in

      #{id}



GROUP BY FUNC_ID

mapper.update:
update task_center set TASK_STATUS=1, DO_STATUS =3 ,END_TIME = #{endTime,jdbcType=TIMESTAMP}


    AND RECODE_ID = #{recodeId,jdbcType=INTEGER}


    AND FUNC_ID = #{funcId,jdbcType=INTEGER}


    AND USER_ID = #{userId,jdbcType=VARCHAR}


    AND DO_URL = #{doUrl,jdbcType=VARCHAR}
