SELECT
n.NEWS_ID,n.SUBJECT,n.TYPE_ID,n.NEWS_TIME,n.CLICK_COUNT,n.PROVIDER,n.CONTENT,n.READERS,u.USER_NAME,n.PRIV_ID,
n.USER_ID,n.TO_ID,n.PUBLISH,n.ANONYMITY_YN,u.AVATAR,u.SEX,n.TOP,u.UID,
n.AUDITER_STATUS,n.AUDITER_TIME,n.AUDITER,n.RUN_ID,n.AUTHOR,n.PHOTOGRAPHER,n.TITLE_PIC_ID,n.TITLE_PIC_NAME
FROM news n
LEFT JOIN `user` u  ON n.PROVIDER = u.USER_ID
LEFT JOIN department d ON u.DEPT_ID = d.DEPT_ID


		AND DATE(n.NEWS_TIME) = #{nTime}


		AND n.FORMAT = #{format}


		AND n.PROVIDER = #{provider}

	 AND (n.TYPE_ID = '' OR  n.TYPE_ID IS  NULL ) 
	 AND n.TYPE_ID = #{typeId} 

		AND n.TOP = #{top}


		AND n.PUBLISH = #{publish}


		AND n.SUBJECT like CONCAT('%',#{subject},'%')


		AND DATE(n.NEWS_TIME) between #{newsTime} and #{lastEditTime}


		AND n.CONTENT like CONCAT('%',#{content},'%')


		AND n.CLICK_COUNT between #{clickCount} and #{click}


		AND n.AUDITER_STATUS = #{auditerStatus}


		AND u.DEPT_ID IN

			#{deptId}



ORDER BY n.TOP DESC,n.NEWS_TIME DESC

