select
n.NEWS_ID,n.SUBJECT,n.TYPE_ID,n.NEWS_TIME,n.PROVIDER,n.READERS,n.PRIV_ID,n.USER_ID,n.TO_ID,
n.PUBLISH,n.TOP,n.AUDITER_STATUS,n.AUDITER_TIME,n.AUDITER
FROM news n,`user` u  WHERE u.user_id = n.PROVIDER  AND  n.PUBLISH=1 AND (

	n.TO_ID='ALL_DEPT' OR

		(n.PRIV_ID LIKE concat('%,',#{userPriv},',%')
		OR n.PRIV_ID LIKE concat(#{userPriv},',%')
		)OR


		(n.TO_ID LIKE concat('%,',#{deptId},',%')
		OR n.TO_ID LIKE concat(#{deptId},',%')
		)OR


		(n.USER_ID LIKE concat('%,',#{userId},',%')
