		)OR



			(n.TO_ID LIKE concat('%,',#{id},',%')
			OR n.TO_ID LIKE concat(#{id},',%')
			)

		or



			(n.PRIV_ID LIKE concat('%,',#{id},',%')
			OR n.PRIV_ID LIKE concat(#{id},',%')
