		OR n.TO_ID LIKE concat(#{deptId},',%')
		)OR


		(n.USER_ID LIKE concat('%,',#{userId},',%')
		OR n.USER_ID LIKE concat(#{userId},',%')
		)OR



			(n.TO_ID LIKE concat('%,',#{id},',%')
			OR n.TO_ID LIKE concat(#{id},',%')
			)

		or



			(n.PRIV_ID LIKE concat('%,',#{id},',%')
			OR n.PRIV_ID LIKE concat(#{id},',%')
			)


)
 AND (n.TYPE_ID = '' OR  n.TYPE_ID IS  NULL ) 
 AND n.TYPE_ID = #{typeId} 
AND 	( n.READERS LIKE concat('%,',#{name},',%')
OR  n.READERS LIKE concat(#{name},',%')
)

	AND n.AUDITER_STATUS = #{auditerStatus}

ORDER BY n.TOP DESC,n.NEWS_TIME DESC

mapper.update:
update news set

	TOP = #{top}


	PUBLISH = #{publish}

WHERE  NEWS_ID  in

	#{id}

update news set

	"TOP" = #{top}


	PUBLISH = #{publish}

WHERE  NEWS_ID  in

	#{id}

mapper.select:
select
n.*
from news n left join flow_run fr on n.RUN_ID=fr.RUN_ID where n.AUDITER = #{userId}

	/*状态为0是待审批的状态。现新加流程审批，状态值为3。都属于待审批，所以添加以下判断，查询流程是否已经结束*/
	and n.AUDITER_STATUS = #{auditerStatus}
	or (trim(fr.RUN_ID)!= '' and n.AUDITER_STATUS =3 and fr.END_TIME IS NOT NULL and fr.END_TIME
	 <>  '0000-00-00 00:00:00')


	and n.AUDITER_STATUS in (1,2)

order by n.NEWS_TIME desc

select
n.*
from news n left join flow_run fr on n.RUN_ID=fr.RUN_ID where n.AUDITER = #{userId}

	/*状态为0是待审批的状态。现新加流程审批，状态值为3。都属于待审批，所以添加以下判断，查询流程是否已经结束*/
	and n.AUDITER_STATUS = #{auditerStatus}
	or (trim(fr.RUN_ID)!= '' and n.AUDITER_STATUS =3 and fr.END_TIME IS NOT NULL)


	and n.AUDITER_STATUS in (1,2)

order by n.NEWS_TIME desc
